INSERT INTO `tbl_contest` (`a_id`, `a_type`, `a_title`, `target`, `a_content`, `a_date`, `add_time`, `year_id`, `order`, `contype`) VALUES
(48, 2, '2013-2014   <律己助人好公民>   歌詞創作比賽', NULL, '<p class="MsoNormal">\r\n	<b>20</b><b>13</b><b>-20</b><b>14</b><b>&nbsp;&nbsp;\r\n&lt;</b><b>律己助人好公民<span>&gt;&nbsp;&nbsp; </span>歌詞創作比賽<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>最佳歌詞創作<span>&nbsp; </span>各級得獎作品<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>&nbsp;</b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：音樂課</b><b>(Twinkle twinkle little\r\nstar)&nbsp;&nbsp; </b><b>1A</b><b>吳晉穎 及 家長陳凱玲</b><b></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：做個好公民<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	做個有愛心公民，快快樂樂幫助人，\r\n</p>\r\n<p class="MsoNormal">\r\n	時常律己守規則，爸媽教誨記在心。\r\n</p>\r\n<p class="MsoNormal">\r\n	常常助人我開心，爸媽老師樂在心。\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：音樂課</b><b>(Twinkle twinkle little\r\nstar)&nbsp;&nbsp; </b><b>2B</b><b>李曉明 及 家長羅漢林<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：律己助人好公民</b><b></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	1.&nbsp;&nbsp;&nbsp;&nbsp; 大家都來齊參與，做個香港好公民，首先就要做的事，堅守紀律遵校訓。\r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	德智體靈共發展，弟子規來作指引。\r\n</p>\r\n<p class="MsoNormal">\r\n	2.&nbsp;&nbsp;&nbsp;&nbsp; 別人意見需聽取，言行舉止要謹慎，得人相助要感恩，彼此關心敬如賓，\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 互相幫助又互愛，我們都是快樂人。\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：音樂課</b><b>(Twinkle twinkle little\r\nstar)&nbsp;&nbsp; </b><b>3C</b><b>甘嘉亮 及 家長區惠貞<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：人人學英模，長大建奇功<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	1.&nbsp;&nbsp;&nbsp;&nbsp; 我們都是好孩子，應從小就來助人，\r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	微笑對人樂於助人，文明禮貌整潔衛生。\r\n</p>\r\n<p class="MsoNormal" style="text-indent:24.0pt;">\r\n	關心他人尊敬師長，做個品學兼優生。\r\n</p>\r\n<p class="MsoNormal">\r\n	2.&nbsp;&nbsp;&nbsp;&nbsp; 我們都是社會棟樑，應從小就學模樣，\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 齊講究社會公德心，愛我祖國愛我社會，\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 全民社會團結起來，一齊締造好家園。\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：採蓮謠</b><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>4C</b><b>梁智杰</b><b></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：律己助人好公民<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	有禮貌，說聲早，大家來做好公民。\r\n</p>\r\n<p class="MsoNormal">\r\n	說聲早，大家讚，人人說我好寶寶。\r\n</p>\r\n<p class="MsoNormal">\r\n	你說一聲，我稱好，他說一聲，大家讚。\r\n</p>\r\n<p class="MsoNormal">\r\n	有禮貌，好寶寶，大家齊來說聲好。\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：真的愛你</b><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>5A</b><b>黃宇玉<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：律己助人好公民<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	好公民應該做的事情，就是看到有需要的人，\r\n</p>\r\n<p class="MsoNormal">\r\n	無論自己會有多忙，也要應該幫助他(她)。\r\n</p>\r\n<p class="MsoNormal">\r\n	如果他們真的忘記了，應該上前要告訴他們。\r\n</p>\r\n<p class="MsoNormal">\r\n	好公民的規則就是幫人應該幫到底。\r\n</p>\r\n<p class="MsoNormal">\r\n	我們應該繼續傳溫暖，不但只有我們的香港。\r\n</p>\r\n<p class="MsoNormal">\r\n	*是它給我知道的意義，教我變得滿有愛心，\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;叮囑我繼續做好公民。(*)\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：北京東路的日子<span>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<a name="_GoBack"></a>&nbsp;</span></b><b>6D </b><b>周鵬超 李澤謙 劉浚傑 劉永維 禤家熙</b><b></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：我們都是好公民<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	開始的開始 我們都是孩子<span>&nbsp;&nbsp; </span>最後的最後 擁有燦爛光明\r\n</p>\r\n<p class="MsoNormal">\r\n	歌聲的裏面 包含做人的道理 我們每個人 都要做好公民\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	開始的開始 我們都是孩子<span>&nbsp;&nbsp; </span>最後的最後 擁有燦爛光明<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	歌聲的裏面 包含做人的道理\r\n我們每個人 都要做好公民<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	當某天 你若聽見 有人在說那些讚美的語言<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	當某天 <span>你若看見 我們大家都非常的快樂</span><span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	這些天 還一樣 我們都是在同一個的地方\r\n</p>\r\n<p class="MsoNormal">\r\n	這些天 再踏進 這城市就是如此美好\r\n如此的和諧安寧\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	表示我們共同努力的成果 社會得以和諧<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	助人爲樂誠實守信等等 我們能做到\r\n</p>\r\n<p class="MsoNormal">\r\n	各種和棘手的社會問題 都迎刃而解<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	我們都要遵守法律的規定 去做好每件事<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	別人若碰到麻煩或挫折 大家要去安慰和幫忙\r\n</p>\r\n<p class="MsoNormal">\r\n	各種違法犯規的事 都不能嘗試\r\n</p>\r\n<p class="MsoNormal">\r\n	以上各好公民行爲 定要時刻緊記<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	各種嘲笑和諷刺 祇說句 嘻嘻一些\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	十年後 你若聽見 有人在說那些讚美的語言<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	十年後 你若看見 我們大家都非常的快樂<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	這些天 還一樣 我們都是在同一個的地方<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	這些天 再踏進 這城市就是如此美好\r\n如此的和諧安寧<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	表示我們共同努力的成果 社會得以和諧<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	助人爲樂誠實守信等等 我們能做到<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	各種和棘手的社會問題 都迎刃而解<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	我們都要遵守法律的規定 去做好每件事<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	我們將來也許 <span>獨自浪在世界各國不同地方</span><span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	但我們絕對不會忘記當初定下的承諾<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	當然我們也不忘記 <span>做個好公民</span><span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	等到世界和平的那個時候都說\r\n我們都是好公民<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	開始的開始 我們都是孩子<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	最後的最後 擁有燦爛光明<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	歌聲的裏面 包含做人的道理<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	我們每個人 都要做好公民<span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>20</b><b>13</b><b>-20</b><b>14</b><b>&nbsp;\r\n&nbsp;&lt;</b><b>律己助人好公民<span>&gt; &nbsp;&nbsp;</span>歌詞創作比賽<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌詞創作 &nbsp;各級優秀作品<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>&nbsp;</b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：音樂課</b><b>(Twinkle twinkle little\r\nstar)&nbsp;&nbsp; </b><b>1B</b><b>潘威銘 及 家長潘國昆</b><b></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：好公民<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	遵守道德好習慣，微笑待人有禮貌，\r\n</p>\r\n<p class="MsoNormal">\r\n	互相幫助樂助人，愛護公物齊做到。\r\n</p>\r\n<p class="MsoNormal">\r\n	大家一起來做個，人人稱讚好公民。\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：音樂課</b><b>(Twinkle twinkle little\r\nstar)&nbsp;&nbsp; </b><b>1C</b><b>周殷穎 及 家長梁蕙梅<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：做個好公民</b><b></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	齊來做個好公民，熱心助人真高興，\r\n</p>\r\n<p class="MsoNormal">\r\n	互助互愛好品德，一起創出好家園，\r\n</p>\r\n<p class="MsoNormal">\r\n	齊來守法又自律，人見人愛就是你。\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：音樂課</b><b>(Twinkle twinkle little\r\nstar)&nbsp;&nbsp; </b><b>2C</b><b>梁梓燊 及 家長馮雪珍<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：做個誠實的好孩子<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	齊來做個好孩子，樂於助人好公民，\r\n</p>\r\n<p class="MsoNormal">\r\n	天天守規有禮貌，做個誠實好孩子，\r\n</p>\r\n<p class="MsoNormal">\r\n	啦啦啦啦啦啦啦，天天快樂地學習。\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：音樂課</b><b>(Twinkle twinkle little\r\nstar)&nbsp;&nbsp; </b><b>3A</b><b>李子揚 及 家長鄭天帶<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：愛‧奉獻<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	齊齊以愛將心獻，你我不分可互勉，\r\n</p>\r\n<p class="MsoNormal">\r\n	快快樂樂手相牽，分分秒秒互助人，\r\n</p>\r\n<p class="MsoNormal">\r\n	同持一顆真心意，開開心心共相處。\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：歌聲音韻滿路途</b><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;</b><b>4B</b><b>全班同學</b><b></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：律己助人好公民<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	好同學，好同學，我們上課要專心。\r\n</p>\r\n<p class="MsoNormal">\r\n	好學生，好學生，我們要聽老師話。\r\n</p>\r\n<p class="MsoNormal">\r\n	幫助別人相愛護，和睦相處好開心。\r\n</p>\r\n<p class="MsoNormal">\r\n	4B班，4B班，又乖又聽話。\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：多啦</b><b>A</b><b>夢</b><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</b><b>5B</b><b>黃嘉文 麥子康 韋啟華 梁振宗<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：律己助人好公民<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	和諧愉快可達到，有心去做必得到。\r\n</p>\r\n<p class="MsoNormal">\r\n	和諧如意天天歡笑，相親相愛多麼美好。\r\n</p>\r\n<p class="MsoNormal">\r\n	遇到難關一樣笑，我的鼓勵必不少。\r\n</p>\r\n<p class="MsoNormal">\r\n	友愛常在不可爭鬥，決心去做就得到。\r\n</p>\r\n<p class="MsoNormal">\r\n	想朋友陪你飛舞，地球看世界。「嗯！要關心別人呀！」\r\n</p>\r\n<p class="MsoNormal">\r\n	開心笑，誰知道愛心緊要，愛心滿地球。\r\n</p>\r\n<p class="MsoNormal">\r\n	多喜愛，誰都知我真喜愛，DORAEMON (x4)\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>原曲：歲月如歌</b><b>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </b><b>6B</b><b>全班同學<span></span></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<b>歌名：律己助人好公民</b><b>(</b><b>普通話版</b><b>)</b><b></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	在學校&nbsp; 我願意&nbsp; 做好學生本份\r\n</p>\r\n<p class="MsoNormal">\r\n	願意自主學習尊重老師\r\n</p>\r\n<p class="MsoNormal">\r\n	我們愉快學習&nbsp; 成為自律的人\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	社會裏&nbsp; 我樂意&nbsp; 做好公民本份\r\n</p>\r\n<p class="MsoNormal">\r\n	樂意幫助有需要的人\r\n</p>\r\n<p class="MsoNormal">\r\n	盡我所有能力&nbsp; 要實現我的理想\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>\r\n<p class="MsoNormal">\r\n	大家互相幫助&nbsp; 讓社會更和諧\r\n</p>\r\n<p class="MsoNormal">\r\n	屬於你我的責任&nbsp; 應要盡力去做\r\n</p>\r\n<p class="MsoNormal">\r\n	助人可以成為&nbsp; 你我的快樂滿足之源\r\n</p>\r\n<p class="MsoNormal">\r\n	要盡能力&nbsp; 做一個充滿愛心的公民\r\n</p>\r\n<p class="MsoNormal">\r\n	只要大家願意&nbsp; 定能共建美好的家園\r\n</p>\r\n<p class="MsoNormal">\r\n	&nbsp;\r\n</p>', '2014-05-23', '2014-05-23 15:15:03', 0, 9, NULL),
(49, 3, '2012-2013獎學金獲獎名單', NULL, '<p class="MsoNormal" align="center" style="text-align:center;">\r\n	<b>聖公會基愛小學</b><b></b>\r\n</p>\r\n<p class="MsoNormal" align="center" style="text-align:center;">\r\n	<b>2012/2013</b><b>年度</b><b></b>\r\n</p>\r\n<p class="MsoNormal" align="center" style="text-align:center;">\r\n	<b>獎學金獲獎名單</b><b></b>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	<span>(1)&nbsp;&nbsp; </span><b>聖公會傑出學生獎</b><span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	一年級<span>( 1A )<u>&nbsp;&nbsp; </u></span><u>鍾碧蘭</u><u>&nbsp; </u><u><span>&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp; </span>二年級<span>( 2D )<u>&nbsp;&nbsp; </u></span><u>溫嘉穎</u><u>&nbsp;&nbsp; </u><u><span>&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>三年級<span>( 3C )<u>&nbsp;&nbsp; </u></span><u>李凱駿</u><u> <span>&nbsp;&nbsp;</span></u><u><span>&nbsp;&nbsp;&nbsp;</span></u><span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	四年級<span>(4C)<u>&nbsp;&nbsp; </u></span><u>王嘉錡</u><u>&nbsp; </u><u><span>&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp; </span>五年級<span>( 5D )<u>&nbsp; &nbsp;</u></span><u>曾明琪</u><u>&nbsp; </u><u>&nbsp;&nbsp;</u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>六年級<span>( 6D )<u>&nbsp;&nbsp; </u></span><u>何芷瑩</u><u>&nbsp; </u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;</span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	<u><span>&nbsp;</span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	<span>(2)&nbsp;&nbsp; </span><b>馮堯佳</b><b>博士獎學金</b> <span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	一年級<span>( 1B )<u>&nbsp;&nbsp; </u></span><u>詹</u><u>&nbsp; </u><u>雪</u><u>&nbsp; </u><u><span>&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>二年級<span>( 2B )<u>&nbsp;&nbsp; </u></span><u>李美怡</u><u> <span>&nbsp;&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>三年級<span>( 3C )<u>&nbsp;&nbsp; </u></span><u>張嘉樂</u><u> </u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	四年級<span>( 4C )<u>&nbsp;\r\n&nbsp;</u></span><u>何梓軒</u><u>&nbsp;&nbsp;&nbsp;&nbsp; </u><u><span> </span></u><span>&nbsp;&nbsp;&nbsp; </span>五年級<span>( 5D )<u>&nbsp;&nbsp; </u></span><u>梁文雅</u><u>&nbsp; </u><u><span>&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span>六年級<span>( 6D )<u>&nbsp; </u></span><u>&nbsp;</u><u>馮汝倩</u><u> </u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;</span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	<u><span>&nbsp;</span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	<span>(3)&nbsp;&nbsp; </span><b>鄧志成總校長獎學金</b><span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	一年級<span>( 1A )<u>&nbsp;&nbsp; </u></span><u>葉芊譽</u><u>&nbsp;&nbsp; </u><u><span>&nbsp;</span></u><span>&nbsp;&nbsp; </span>二年級<span>( 2C )<u>&nbsp;&nbsp; </u></span><u>董宜季</u><u>&nbsp; </u><u><span>&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp; </span>三年級<span>( 3C )<u>&nbsp;&nbsp; </u></span><u>&nbsp;</u><u>陳曉桐</u><u><span>&nbsp;&nbsp;&nbsp;&nbsp; </span></u><span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	四年級<span>( 4C )<u>&nbsp;&nbsp; </u></span><u>陳佩瑤</u><u>&nbsp;&nbsp; </u><u><span>&nbsp;</span></u><span>&nbsp;&nbsp; </span>五年級<span>( 5D )<u>&nbsp;&nbsp; </u></span><u>郭奕婷</u><u> &nbsp;</u><u>&nbsp;</u><span>&nbsp;&nbsp;&nbsp;&nbsp; </span>六年級<span>( 6D )<u>&nbsp;&nbsp; </u></span><u>&nbsp;</u><u>陳煒棋</u><u><span>&nbsp;&nbsp;&nbsp;&nbsp; </span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	<span>(4)&nbsp;&nbsp; </span><b>關怡清校友獎學金</b> <span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	五年級<span>( 5D )<u>&nbsp;&nbsp; </u></span><u>周鵬超</u><u>&nbsp;&nbsp;&nbsp; </u><u><span>&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span>六年級<span>( 6D )<u>&nbsp;&nbsp; </u></span><u>劉浩楷</u><u><span>&nbsp;&nbsp; </span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	<span>(5)&nbsp;&nbsp; </span><b>陳何偉卿老師獎學金</b> <span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	成績努力進步獎<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp; ( 4B )<u>&nbsp;&nbsp;&nbsp; </u></span><u>林俊賢</u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp; ( 5B )<u>&nbsp;&nbsp;&nbsp;&nbsp; </u></span><u>徐永康</u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	品行明顯改進獎/最佳品行表現獎<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (\r\n5B )<u>&nbsp;&nbsp;&nbsp; </u></span><u>聶黃海</u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp; ( 6B )<u>&nbsp;&nbsp;&nbsp; &nbsp;</u></span><u>許顥寶</u><u> <span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;</span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	體藝表現傑出獎<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp; (6C)<u>&nbsp;&nbsp;&nbsp; </u></span><u>梁暐東</u><u> </u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp; (6C)<u>&nbsp;&nbsp; &nbsp;&nbsp;</u></span><u>鄧</u><u>&nbsp; </u><u>穎</u><u><span>&nbsp;&nbsp;&nbsp;&nbsp; </span></u><u>&nbsp;&nbsp;</u><u><span>&nbsp;&nbsp;</span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	美育才藝突出獎<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp; ( 6B )<u>&nbsp;&nbsp;&nbsp; </u></span><u>邱雪嵐</u><u> </u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (\r\n6D )<u>&nbsp;&nbsp;&nbsp; &nbsp;</u></span><u>何國彬</u><u>&nbsp; </u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	盡心服務熱誠獎<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp; (5A)<u>&nbsp;&nbsp;&nbsp; </u></span><u>謝德濤</u><u>&nbsp;&nbsp; </u><u>&nbsp;&nbsp;</u><u><span>&nbsp;&nbsp;&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (\r\n6B )<u>&nbsp;&nbsp;&nbsp; &nbsp;</u></span><u>蘇卓鋒</u><u>&nbsp;&nbsp;&nbsp;&nbsp; </u><u> </u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	<span>(6)&nbsp;&nbsp; </span><b>陸亦超</b><b>老師獎學金</b> <span></span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>( 5A )<u>&nbsp;&nbsp; </u></span><u>翁沅婷</u><u> </u><u><span>&nbsp;&nbsp;&nbsp;&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;( 4C\r\n)<u>&nbsp;&nbsp; </u></span><u>姚瀚哲</u><u><span>&nbsp;&nbsp;&nbsp; </span></u><span>&nbsp;&nbsp;&nbsp;( 6B )<u> &nbsp;&nbsp;</u></span><u>朱珮瑛</u><u> <span>&nbsp;&nbsp;&nbsp;</span></u><span>&nbsp;&nbsp;&nbsp;( 3B\r\n)<u>&nbsp; </u></span><u>鄺世亮</u><u><span>&nbsp; </span></u>\r\n</p>\r\n<p class="MsoNormal">\r\n	<u>&nbsp;</u>\r\n</p>\r\n<p class="MsoNormal">\r\n	<u>&nbsp;</u>\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	<span>(7)&nbsp;&nbsp; </span><b>第十六屆校友獎學金</b><b><span>(</span></b><b>個人才藝卓越獎</b><b><span>)</span></b><span> </span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>( 2B )<u>&nbsp; </u></span><u>李子揚</u><span>&nbsp;&nbsp; ( 6B )<u>&nbsp; </u></span><u>梁振聰</u><u> </u><u></u>\r\n</p>\r\n<br />', '2014-05-23', '2014-05-23 15:53:57', 0, 1, NULL);
