INSERT INTO `tbl_stuteacher` (`a_id`, `a_type`, `a_title`, `link`, `target`, `a_content`, `a_encontent`, `a_cncontent`, `a_entitle`, `a_cntitle`, `a_date`, `add_time`, `year_id`, `order`) VALUES
(1, 1, '數學	', 'http://', '_blank', '<div class="item-page">\r\n	<div class="page-header">\r\n		<h2>\r\n			數學科\r\n		</h2>\r\n	</div>\r\n<!-- supertable debug output all exec 0.00003 gen 0.00001 tot 0.00004 end supertable debug output -->\r\n	<p>\r\n		<strong>(一) 教學目標</strong> \r\n	</p>\r\n	<ol>\r\n		<li>\r\n			引起兒童對數學學習的興趣。\r\n		</li>\r\n		<li>\r\n			啟發兒童的數學思考，培養兒童的創造力及解難能力。\r\n		</li>\r\n		<li>\r\n			教授基本的數學概念及計算技巧為中學的數學及科學的學習奠好基礎。\r\n		</li>\r\n		<li>\r\n			讓兒童學習運用數學解決日常生活的問題。\r\n		</li>\r\n		<li>\r\n			引導兒童對數和圖形的規律及結構的欣賞。\r\n		</li>\r\n		<li>\r\n			具有良好的公民意識。\r\n		</li>\r\n	</ol>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong>(二) 教學方法</strong> \r\n	</p>\r\n	<p>\r\n		藉著例題講授、提問、觀察、經驗分享、討論、堂課、分組活動及網上學習等，讓學生學習本科的基本概念和知識。\r\n	</p>\r\n	<p>\r\n		A. 教材\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			課本 - 一至六年級數學（朗文出版社）。\r\n		</li>\r\n		<li>\r\n			進度 - 一至六年級上、下學期各二冊，並依課程綱要建議編訂。全部年級會以單元重新編寫\r\n		</li>\r\n		<li>\r\n			解難手冊 – 一至六年級會依據課題內容，螺旋式地加入不同的解難策略，全年共有七課題，精英班全年共有九課題。\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		B.作業\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			堂課 - 一至六年級堂課簿（試算、心算、補充練習及課本或作業中之練習等）\r\n		</li>\r\n		<li>\r\n			作業簿\r\n		</li>\r\n		<li>\r\n			家課簿（練習次數：<strong><span style="text-decoration:underline;">每週兩次或以上</span></strong>，一年級上學期，可以工作紙冊代替。）\r\n		</li>\r\n		<li>\r\n			補充練習：\r\n		</li>\r\n		<ul>\r\n			<li>\r\n				1-6 年級：工作紙（每星期最少一張）\r\n			</li>\r\n			<li>\r\n				1-6 年級︰TSA分類練習補充\r\n			</li>\r\n			<li>\r\n				課業冊（練習次數：每學期最少6次）\r\n			</li>\r\n			<li>\r\n				解難手冊(全學年共7課)\r\n			</li>\r\n			<li>\r\n				英文數學家課簿(六年級精英班於下學期應用)\r\n			</li>\r\n		</ul>\r\n	</ul>\r\n	<p>\r\n		<strong>(三) 考核方法</strong> \r\n	</p>\r\n	<p>\r\n		1. 考試：各級上、下學期考試各兩次\r\n	</p>\r\n	<p>\r\n		2. 全港性系統評估：小三及小六學生於下學期進行(由教育局安排）\r\n	</p>\r\n	<p>\r\n		3. 日常考核：\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			觀察學生對新課題的反應\r\n		</li>\r\n		<li>\r\n			觀察學生習作的表現\r\n		</li>\r\n		<li>\r\n			利用綜合性練習作為測驗，評估學生對各課題之認識及掌握\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		4. 利用單元估讓學生及教師了解學習進度\r\n	</p>\r\n	<p>\r\n		<strong>(四) 考核範圍</strong> \r\n	</p>\r\n	<p>\r\n		試題範圍包括已有知識、課本內容和思考題、短答案題、列式作答題、情境題、作圖題或看圖作答和統計圖。\r\n	</p>\r\n	<p>\r\n		<strong>(五) 數學活動</strong> \r\n	</p>\r\n	<ol>\r\n		<li>\r\n			「難題易解」：一至六年級\r\n		</li>\r\n		<li>\r\n			數學遊戲\r\n		</li>\r\n		<li>\r\n			「幼稚園奧數比賽」\r\n		</li>\r\n		<li>\r\n			「數學嘉年華」\r\n		</li>\r\n		<li>\r\n			圖書閱讀\r\n		</li>\r\n		<li>\r\n			數學比賽\r\n		</li>\r\n	</ol>\r\n</div>', NULL, NULL, NULL, NULL, '2013-09-10', '2013-11-25 18:13:40', 0, 100),
(11, 1, '常識科', '', NULL, '<div class="item-page">\r\n	<div class="page-header">\r\n		<h2>\r\n			常識\r\n		</h2>\r\n	</div>\r\n<!-- supertable debug output all exec 0.00003 gen 0.00001 tot 0.00003 end supertable debug output -->\r\n	<p>\r\n		<strong>(一) 課程簡介</strong>\r\n	</p>\r\n	<p>\r\n		常識科旨在為學生提供學習經驗，讓他們更了解自己所處的世界，誘發學生的學習興趣,讓學生透過不同學習範疇,建構學科知識、培養學生的各能力以探究與科學、科技及社會有關的課題和事件及培養學生正面的態度知價值觀。\r\n	</p>\r\n	<p>\r\n		<strong>(二) 課程宗旨</strong>\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			常識科課程旨在協助學生：\r\n		</li>\r\n		<li>\r\n			保持健康的個人發展，成為充滿自信，理性和富責任感的公民；\r\n		</li>\r\n		<li>\r\n			認識自己在家庭和社會擔當的角色及應履行的責任，並關注本身的福祉；\r\n		</li>\r\n		<li>\r\n			培養對國民身份的認同感，並致力貢獻國家和世界；\r\n		</li>\r\n		<li>\r\n			培養對自然及科技世界的興趣和好奇心，了解科學與科技發展對社會的影響；\r\n		</li>\r\n		<li>\r\n			關心及愛護周遭的環境；\r\n		</li>\r\n		<li>\r\n			培養探索研究和解決問題的能力。\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		<strong>(三) 學習範圍</strong>\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			了解自己的成長和發育，建立健康的生活方式，尊重自己和別人，並重視和諧的人際關係；\r\n		</li>\r\n		<li>\r\n			了解身處的社區及香港其他社區的發展、特?及其將來潛在的轉變；\r\n		</li>\r\n		<li>\r\n			關心家人、香港社會、中國以至整個世界；\r\n		</li>\r\n		<li>\r\n			對研習物質世界、能源運用、生物、地球以至宇宙間事物的科學問題產生興趣，並培養探索、研究和尋找有關答案的能力；\r\n		</li>\r\n		<li>\r\n			對探索科技世界產生興趣及懂得有創意地運用科技知識，以解決日常生活中簡單的問題；\r\n		</li>\r\n		<li>\r\n			了解科學及科技發展對人類社會和環境可能產生的負面影響，並培養保護環境的責任感。\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>', NULL, NULL, NULL, NULL, '0000-00-00', '0000-00-00 00:00:00', 0, 100),
(12, 1, '普通話科', '', NULL, '<div class="item-page">\r\n	<div class="page-header">\r\n		<h2>\r\n			普通話科\r\n		</h2>\r\n	</div>\r\n<!-- supertable debug output all exec 0.00003 gen 0.00001 tot 0.00004 end supertable debug output -->\r\n	<p>\r\n		<strong>(一) 課程簡介</strong>\r\n	</p>\r\n	<p>\r\n		本科的學習分為聆聽、說話、閱讀及譯寫四個範疇，每個學習範疇均有其學習目標，稱為「學習範疇的學習目標」。本科課程內容的編排由淺入深，緊密照應，使學生有明確的學習方向，能循序漸進地學習。\r\n	</p>\r\n	<table style="width:584px;" border="1" cellspacing="0" cellpadding="0">\r\n		<tbody>\r\n			<tr>\r\n				<td width="195">\r\n				</td>\r\n				<td width="195">\r\n					<p align="center">\r\n						第一學習階段\r\n					</p>\r\n					<p align="center">\r\n						（小一至小三）\r\n					</p>\r\n				</td>\r\n				<td width="195">\r\n					<p align="center">\r\n						第二學習階段\r\n					</p>\r\n					<p align="center">\r\n						（小四至小六）\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="195">\r\n					<p align="center">\r\n						聆聽教學\r\n					</p>\r\n					<p align="center">\r\n						說話教學\r\n					</p>\r\n					<p align="center">\r\n						閱讀教學\r\n					</p>\r\n				</td>\r\n				<td width="195">\r\n					<p align="center">\r\n						約佔教時的三分之二\r\n					</p>\r\n				</td>\r\n				<td width="195">\r\n					<p align="center">\r\n						約佔教時的三分之二\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="195">\r\n					<p align="center">\r\n						譯寫教學\r\n					</p>\r\n				</td>\r\n				<td width="195">\r\n					<p align="center">\r\n						約佔教時的三分之一\r\n					</p>\r\n				</td>\r\n				<td width="195">\r\n					<p align="center">\r\n						約佔教時的三分之一\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<strong>(二) 課程宗旨</strong>\r\n	</p>\r\n	<ol>\r\n		<li>\r\n			培養聆聽、說話、朗讀等語言能力，以及自學能力。\r\n		</li>\r\n		<li>\r\n			增進與本科有關的語音知識，以及對<span style="text-decoration:underline;">中國</span>文化的認識。\r\n		</li>\r\n		<li>\r\n			提高對本科的學習興趣，培養良好的學習態度和習慣。\r\n		</li>\r\n	</ol>\r\n	<p>\r\n		<strong>(三) 學習範疇的學習目標</strong>\r\n	</p>\r\n	<table style="width:560px;" border="1" cellspacing="0" cellpadding="0">\r\n		<tbody>\r\n			<tr>\r\n				<td colspan="2" width="560">\r\n					<p align="center">\r\n						學習範疇的學習目標\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="40">\r\n					<p align="center">\r\n						聆聽\r\n					</p>\r\n				</td>\r\n				<td width="520">\r\n					<p>\r\n						培養聆聽能力，以理解話語的內容；培養聆聽的興趣及良好的聆聽態度。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="40">\r\n					<p align="center">\r\n						說話\r\n					</p>\r\n				</td>\r\n				<td width="520">\r\n					<p>\r\n						培養說話能力，以表達自己的思想感情；培養說話的興趣及良好的說話態度和習慣。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="40">\r\n					<p align="center">\r\n						閱讀\r\n					</p>\r\n				</td>\r\n				<td width="520">\r\n					<p>\r\n						培養朗讀及自學能力；增進與本科有關的語言知識，以及對<span style="text-decoration:underline;">中國</span>文化的認識；培養閱讀興趣及良好的閱讀態度和習慣。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="40">\r\n					<p align="center">\r\n						譯寫\r\n					</p>\r\n				</td>\r\n				<td width="520">\r\n					<p>\r\n						培養運用漢語拼音的能力，以提高自學能力。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<strong><br />\r\n(四) 學習範圍</strong>\r\n	</p>\r\n	<table border="1" cellspacing="0" cellpadding="0">\r\n		<tbody>\r\n			<tr>\r\n				<td valign="top" width="279">\r\n					<p align="center">\r\n						<strong>年級</strong>\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="279">\r\n					<p align="center">\r\n						<strong>課程內容</strong>\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign="top" width="279">\r\n					<p align="center">\r\n						第一學習階段\r\n					</p>\r\n					<p align="center">\r\n						(小一至小三)\r\n					</p>\r\n					<p align="center">\r\n						基礎訓練\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="279">\r\n					<p>\r\n						認識聲調、聲母和韻母，學習標調規則、輕聲、「一」的變   調、「不」的變調、三聲字連讀，兒化韻、四聲連讀、音節拼讀和短句拼讀。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign="top" width="279">\r\n					<p align="center">\r\n						第二學習階段\r\n					</p>\r\n					<p align="center">\r\n						(小四至小六)\r\n					</p>\r\n					<p align="center">\r\n						正音與應用\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="279">\r\n					<p>\r\n						針對難點練習，分辨容易混淆的聲母、韻母，鞏固輕聲、兒化韻和三聲字連讀變調的唸法，學習一般拼寫規則以及讀說技巧。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<br />\r\n<strong>(五) 評估範圍</strong>\r\n	</p>\r\n	<p>\r\n		1. 持續性評估:\r\n	</p>\r\n	<p>\r\n		每學期按學習重點進行兩次持續評估,全年共四次。\r\n	</p>\r\n	<p>\r\n		評估內容包括聆聽、語音及語法知識。\r\n	</p>\r\n	<p>\r\n		2. 學期終結評估:每學期進行期考一次,全年共兩次。\r\n	</p>\r\n	<p>\r\n		學期終結評估重點:\r\n	</p>\r\n	<table style="width:585px;height:151px;" border="1" cellspacing="0" cellpadding="0">\r\n		<tbody>\r\n			<tr>\r\n				<td width="189">\r\n					<p align="center">\r\n						考核重點\r\n					</p>\r\n					<p align="center">\r\n						（一至六年級）\r\n					</p>\r\n				</td>\r\n				<td width="396">\r\n					<p align="center">\r\n						分額百分比\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="189">\r\n					<p align="center">\r\n						聆聽能力\r\n					</p>\r\n				</td>\r\n				<td width="396">\r\n					<p align="center">\r\n						30%\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="189">\r\n					<p align="center">\r\n						語音及\r\n					</p>\r\n					<p align="center">\r\n						語法知識\r\n					</p>\r\n				</td>\r\n				<td width="396">\r\n					<p align="center">\r\n						30%\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="189">\r\n					<p align="center">\r\n						說話能力\r\n					</p>\r\n				</td>\r\n				<td width="396">\r\n					<p align="center">\r\n						20%\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="189">\r\n					<p align="center">\r\n						日常表現\r\n					</p>\r\n				</td>\r\n				<td width="396">\r\n					<p align="center">\r\n						20%\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>', NULL, NULL, NULL, NULL, '0000-00-00', '0000-00-00 00:00:00', 0, 100),
(13, 1, '視覺藝術', '', NULL, '<div class="item-page">\r\n	<div class="page-header">\r\n		<h2>\r\n			視覺藝術\r\n		</h2>\r\n	</div>\r\n<!-- supertable debug output all exec 0.00003 gen 0.00001 tot 0.00004 end supertable debug output -->\r\n	<p>\r\n		<strong>(一) </strong><strong>課程簡介</strong>\r\n	</p>\r\n	<p>\r\n		根據香港課程發展議會編訂的「藝術教育學習領域視覺藝街科課程指引」(小一至中三)(2003)視覺藝術科的學習目標包括﹕\r\n	</p>\r\n	<ol>\r\n		<li>\r\n			培養創造力及想像力；\r\n		</li>\r\n		<li>\r\n			發展技能與過程；\r\n		</li>\r\n		<li>\r\n			培養評賞藝術的能力；\r\n		</li>\r\n		<li>\r\n			認識藝術的情境。\r\n		</li>\r\n	</ol>\r\n	<ul>\r\n		<li>\r\n			學生透過課堂學習和評估活動，發展<a href="http://zh.wikipedia.org/wiki/%E8%A6%96%E8%A6%BA%E8%97%9D%E8%A1%93" title="視覺藝術">視覺藝術</a>方面的知識、技能和態度︰\r\n		</li>\r\n		<li>\r\n			課程：學校宜參考課程指引，再根據本身的情況，自行設計校本視藝課程。\r\n		</li>\r\n		<li>\r\n			教學：鼓勵學生主動學習及進行探究，而非由教師單向傳授知識。<br />\r\n評估：運用多元化評估模式，每項評估須有清晰的評估範疇和準則。\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		<strong>(二) </strong><strong>課程宗旨</strong>\r\n	</p>\r\n	<ol>\r\n		<li>\r\n			擴展學生以視覺藝術為主的感官、美感和藝術經驗，發展學生視覺認知、陶冶學生情意、修為、品德，以至對國家民族及世界的投入感。\r\n		</li>\r\n		<li>\r\n			培養學生的創意及想像力、發展創作技能與表達能力、評賞藝術的能力及認識藝術的情境，掌握和運用共通能力。\r\n		</li>\r\n		<li>\r\n			根據學生的身心發展，通過適當的創作活動，使其認識造形藝術的創作概念及在材料的性質和工具操作上提供各種探討、試驗的機會，以表達其對事物的感受。\r\n		</li>\r\n		<li>\r\n			使學生養成高尚的情操，均衡發展的人格，良好的生活態度，將來在不斷變化的複雜社會中，有信心和堅定的意志，努力創造更美好的環境。\r\n		</li>\r\n		<li>\r\n			通過視覺藝術學習，包括視覺藝術知識、評賞及創作，並在過程中學會如何學會學習；探討解決問題的方法，為終身學習的教學宗旨奠定基礎。\r\n		</li>\r\n	</ol>\r\n	<p>\r\n		<strong>(三) </strong><strong>學習範圍</strong>\r\n	</p>\r\n	<p>\r\n		每個年級的教學計劃設計都必須包括三大範疇﹕視覺藝術知識、視覺藝術評賞及視覺藝術創作。視藝科教材可作多方面創作去實施計劃，如繪畫、藝術欣賞、設計及其他立體創作等等。每學年開始當由任教該級之視藝科教師共同訂定該學年之教學計劃，而教材應與節日及課程綱要配合。本科每學年均會編訂各級的教學進度，各級教學內容請參考《視覺藝術科進度表》。\r\n	</p>\r\n	<p>\r\n		<br />\r\n<strong>(四) </strong><strong>近期活動</strong>\r\n	</p>\r\n	<p>\r\n		參觀香港教育學院及城巴舉辦「Arts Bus：On the  move”Road Safety” Bus Design Competition2011」 5D連樂君同學榮獲出色表現獎，更應邀出席TVB兒童節目「放學ICU」，在節目中接受訪問。\r\n	</p>\r\n</div>', NULL, NULL, NULL, NULL, '0000-00-00', '0000-00-00 00:00:00', 0, 100),
(14, 1, '體育科', '', NULL, '<div class="item-page">\r\n	<div class="page-header">\r\n		<h2>\r\n			體育科\r\n		</h2>\r\n	</div>\r\n<!-- supertable debug output all exec 0.00003 gen 0.00001 tot 0.00004 end supertable debug output -->\r\n	<p>\r\n		<strong>(</strong><strong>一)</strong> <strong>課程簡介</strong>\r\n	</p>\r\n	<p>\r\n		透過體育活動增進兒童之健康，促進神經、肌肉、骨骼及身體各部官能，使身體獲致全面的發展及良好的體適能。提供各種遊戲和體育活動，以培養兒童高尚之品德，公正之態度，良好及進取的體育精神。\r\n	</p>\r\n	<p>\r\n		<strong>(</strong><strong>二) 課程宗旨</strong>\r\n	</p>\r\n	<ol>\r\n		<li>\r\n			教授各種遊戲及體育活動，使兒童明白互相合作在團體生活的重要；同時培養兒童對團體的責任感和歸屬感。\r\n		</li>\r\n		<li>\r\n			透過舞蹈、體操等的活動，培養兒童優美的體態，加強他們對優美事物例如動作、儀態等欣賞。\r\n		</li>\r\n		<li>\r\n			使兒童在活動過程中，加強他們的觀察、分析、判斷、適應和創作的能力。\r\n		</li>\r\n		<li>\r\n			培養兒童對體育活動有良好之興趣和態度，使其積極地在日常生活中參與運動和鍛鍊體魄，養成日後參加正當休閒活動的習慣。\r\n		</li>\r\n	</ol>\r\n	<p>\r\n		<strong>(</strong><strong>三) 學習範圍</strong><br />\r\n學生除了每星期兩節體育課外，校方亦安排大量不同體育性質的課外活動讓學生參加，讓學生有更大的參與量和運動量。<br />\r\n體育組每年均派隊參加各項校外比賽及參與不同的體育活動。<strong><br />\r\n</strong>\r\n	</p>\r\n	<p>\r\n		<strong>(</strong><strong>四</strong><strong>) </strong><strong>近期活動</strong>\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			校運會\r\n		</li>\r\n	</ul>\r\n</div>', NULL, NULL, NULL, NULL, '0000-00-00', '0000-00-00 00:00:00', 0, 100),
(2, 2, '電腦科', 'http://', '_blank', '<div class="item-page">\r\n	<div class="page-header">\r\n		<h2>\r\n			電腦科\r\n		</h2>\r\n	</div>\r\n<!-- supertable debug output all exec 0.00003 gen 0.00001 tot 0.00004 end supertable debug output -->\r\n	<p>\r\n		<strong>(一) 課程簡介</strong>\r\n	</p>\r\n	<p>\r\n		本校電腦課程按照香港課程發展議會編訂的「資訊科技學習目標」，致力協助學生掌握資訊科技的知識、技能和態度，並培養高層次的思維技巧和處理資訊（尋找、評估、組織和表達）的方法。\r\n	</p>\r\n	<p>\r\n		<strong>(二) 課程宗旨</strong>\r\n	</p>\r\n	<p>\r\n		主要教學目標\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			通過認識電腦與生活的關係，培養學生的資訊素養，從而建立正確的價值觀。\r\n		</li>\r\n		<li>\r\n			利用互聯網絡，拓展個人與外界之關係，培養學生的世界觀,使其能放眼世界。\r\n		</li>\r\n		<li>\r\n			通過運用電腦協助處理日常生活事務和學習，使學生建立成就感，培養獨立思考判斷能力。\r\n		</li>\r\n		<li>\r\n			通過多媒體操作與應用，激發學生的潛能，發展學生的推理、想像和創作能力。\r\n		</li>\r\n		<li>\r\n			使學生養成收集資訊、分類資訊和利用資訊的良好習慣，培養正確的學習態度，以期達致自學不倦的教育目標。\r\n		</li>\r\n		<li>\r\n			誘導學生學習利用網誌表達意見，促進知識交流。\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		<strong>(三) 學習範圍</strong>\r\n	</p>\r\n	<table border="1" cellspacing="0" cellpadding="0">\r\n		<tbody>\r\n			<tr>\r\n				<td rowspan="2" valign="top" width="117">\r\n					<p>\r\n						年級\r\n					</p>\r\n				</td>\r\n				<td colspan="3" valign="top">\r\n					<p align="center">\r\n						範疇\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign="top" width="104">\r\n					<p align="center">\r\n						知識\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="123">\r\n					<p align="center">\r\n						技能\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="124">\r\n					<p align="center">\r\n						態度\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign="top" width="117">\r\n					<p>\r\n						一至三年級\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="104">\r\n					<p>\r\n						指出學校提供的一般資訊科技工具名稱； <br />\r\n認識這些資訊科技工具的功能； <br />\r\n指出日常生活中應用資訊科技的實例。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="123">\r\n					<p>\r\n						操作校內的電腦； <br />\r\n用手寫識別裝置輸入中文； <br />\r\n在教師的協助下，用多媒體資源協助學習； <br />\r\n在學習活動中，用資訊科技工具與人溝通及處理資訊。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="124">\r\n					<p>\r\n						培養使用資訊科技的興趣和正確態度； <br />\r\n與同學互相合作； <br />\r\n適當地使用資訊科技。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign="top" width="117">\r\n					<p>\r\n						四至六年級\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="104">\r\n					<p>\r\n						懂得說出一些常見資訊科技應用的正確名稱； <br />\r\n了解資訊科技在這些應用的重要性。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="123">\r\n					<p>\r\n						用各種軟件進行文字處理、計算、圖像處理及其他學習活動； <br />\r\n用手寫識別裝置及中文輸入法輸入中文； <br />\r\n透過電腦網絡及其他媒體獲取資料； <br />\r\n用資訊科技工具處理資訊。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="124">\r\n					<p>\r\n						認識知識產權、版權和私隱權； <br />\r\n提防電腦網絡及其他媒體中的不良成分； <br />\r\n認識使用互聯網時有需要保護自己； <br />\r\n願意與同學分享意見。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>', NULL, NULL, NULL, NULL, '2013-09-10', '2013-11-25 18:13:43', 0, 100),
(3, 1, '宗教科', 'http://', '_blank', '<div class="item-page">\r\n	<div class="page-header">\r\n		<h2>\r\n			宗教科\r\n		</h2>\r\n	</div>\r\n<!-- supertable debug output all exec 0.00003 gen 0.00001 tot 0.00004 end supertable debug output -->\r\n	<p>\r\n		<strong> (一) 課程宗旨</strong>\r\n	</p>\r\n	<ol>\r\n		<li>\r\n			根據聖經教導學生認識基督的真理。\r\n		</li>\r\n		<li>\r\n			引導學生接受主耶穌基督，成為他們生命的主。\r\n		</li>\r\n		<li>\r\n			教導學生實踐信仰生活，愛讀聖經，凡事祈禱、謝恩。\r\n		</li>\r\n		<li>\r\n			引領更多學生到基愛堂或其他教會建立教會生活。\r\n		</li>\r\n	</ol>\r\n	<p>\r\n		<strong> (二) 學習範疇</strong>\r\n	</p>\r\n	<p>\r\n		透過不同範疇，宣揚基督的訊息\r\n	</p>\r\n	<table style="width:370px;height:143px;" border="1" cellspacing="0" cellpadding="0">\r\n		<tbody>\r\n			<tr>\r\n				<td style="text-align:center;" valign="top" width="117">\r\n					<p align="center">\r\n						範疇\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="472">\r\n					<p align="center">\r\n						活動\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style="text-align:center;" valign="top" width="117">\r\n					<p>\r\n						敬拜\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="472">\r\n					<ol>\r\n						<li>\r\n							節期性崇拜(救主聖誕崇拜、復活感恩崇拜);\r\n						</li>\r\n						<li>\r\n							畢業感恩崇拜;\r\n						</li>\r\n						<li>\r\n							早、午禱崇拜\r\n						</li>\r\n					</ol>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style="text-align:center;" valign="top" width="117">\r\n					<p>\r\n						佈道\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="472">\r\n					<p>\r\n						邀請基督教機構到校舉辦佈道會\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style="text-align:center;" valign="top" width="117">\r\n					<p>\r\n						聖經故事及金句\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="472">\r\n					<p>\r\n						宗教課,每週一節\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style="text-align:center;" valign="top" width="117">\r\n					<p>\r\n						畢業生靈命栽培\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="472">\r\n					<p>\r\n						<span style="text-decoration:underline;">基愛堂</span>「青苗團契」\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style="text-align:center;" valign="top" width="117">\r\n					<p>\r\n						家長活動\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="472">\r\n					<p>\r\n						「樂愛之家」\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td style="text-align:center;" valign="top" width="117">\r\n					<p>\r\n						制服團隊\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="472">\r\n					<p>\r\n						「基督少年軍」\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<ul style="list-style-type:square;">\r\n		<li>\r\n			<strong>學生培育</strong>\r\n		</li>\r\n	</ul>\r\n	<p style="padding-left:30px;">\r\n		<strong>「教養孩童，使他走當行的道，就是到老他也不偏離。</strong>」《<strong>箴言22:6</strong>》我們重視學生的靈命成長，透過宗教課堂、早、午禱崇拜、節期性感恩崇拜及佈道會等，讓學生學習聖經故事和福音詩歌，更從讀聖經、祈禱及分享見證中去經歷上帝的恩典。<span style="text-decoration:underline;">基愛堂</span>牧師及傳道更定期到本校主領聚會，傳講福音訊息。在課外活動方面，我們設有團契及「基督少年軍」，讓學生更多認識神的話語，順從紀律，做個合神心意的孩子。\r\n	</p>\r\n	<ul style="list-style-type:square;">\r\n		<li>\r\n			<strong>畢業生靈命成長</strong>\r\n		</li>\r\n	</ul>\r\n	<p style="padding-left:30px;">\r\n		基愛堂關心學生的靈命成長，不少本校畢業生在離開母校後，仍定期參與基愛堂活動，在教會裏健康成長，建立積極的人生觀及信仰生命。\r\n	</p>\r\n	<ul style="list-style-type:square;">\r\n		<li>\r\n			<strong>家長活動</strong>\r\n		</li>\r\n	</ul>\r\n	<p style="padding-left:30px;">\r\n		<span style="text-decoration:underline;">基愛堂</span>為本校家長成立「樂愛之家」，定期舉行家長聚會，是家長們分享及交流的好園地。教會每年都為本校家庭舉行福音晚會，邀請家長帶同子女一同參加愛筵。大家一同唱詩，一同遊戲，共享天倫，分享基愛的愛。\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>', NULL, NULL, NULL, NULL, '2013-09-10', '2013-11-25 18:13:51', 0, 100),
(4, 1, '音樂科', 'http://', '_blank', '<div class="item-page">\r\n	<div class="page-header">\r\n		<h2>\r\n			音樂科\r\n		</h2>\r\n	</div>\r\n<!-- supertable debug output all exec 0.00003 gen 0.00001 tot 0.00004 end supertable debug output -->\r\n	<p>\r\n		<strong>(一) 課程簡介</strong>\r\n	</p>\r\n	<table border="1" cellspacing="0" cellpadding="0">\r\n		<tbody>\r\n			<tr>\r\n				<td colspan="5" valign="top">\r\n					<p>\r\n						學習重點\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign="top" width="28">\r\n					<p>\r\n						KS1\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="112">\r\n					<p>\r\n						1.利用基本音樂技能、簡單音樂意念及同聲創作/即興創作音樂。 <br />\r\n2.創作/即興創作律動以反映不同的音樂特質。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="109">\r\n					<p>\r\n						1. 透過背譜或讀譜方式發展歌唱及樂器演奏的基本技巧。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="110">\r\n					<p>\r\n						1.表達個人對音樂的感受。 <br />\r\n2.辨別聲音/音樂的特徵，並運用簡單音樂術語來描述其特色。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="107">\r\n					<p>\r\n						1.描述音樂在日常生活的功能。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td valign="top" width="28">\r\n					<p>\r\n						KS2\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="112">\r\n					<p>\r\n						1.創作/即興創作具結構及組織的音樂。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="109">\r\n					<p>\r\n						1.準確地齊唱及二部合唱。 <br />\r\n2.準確地演奏有固定音高及無固定音高的樂器。 <br />\r\n3.應用五線譜及其他記譜法讀譜及記譜。 <br />\r\n4.運用資訊科技記錄音樂。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="110">\r\n					<p>\r\n						1.描述及分析具簡單結構的音樂。 <br />\r\n2.應用既定準則和適當音樂術語來評賞音樂作品及演出。\r\n					</p>\r\n				</td>\r\n				<td valign="top" width="107">\r\n					<p>\r\n						1.描述不同情境運用人聲/樂器的方法。\r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p>\r\n		註：(參照音樂課程指引P.11)\r\n	</p>\r\n	<p>\r\n		<strong>(二) 課程宗旨</strong>\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			培養創意及想像力\r\n		</li>\r\n	</ul>\r\n	<p style="padding-left:30px;">\r\n		發展音樂意念和掌握創作技巧，配合演奏和聆聽，以培養創意和想像力。\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			發展音樂技能與過程\r\n		</li>\r\n	</ul>\r\n	<p style="padding-left:30px;">\r\n		發展演奏的技巧以體驗和表現音樂，在實踐過程中，重視培養音樂想像力和音樂感。\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			培養評賞音樂的能力\r\n		</li>\r\n	</ul>\r\n	<p>\r\n		理解音樂並作出回應和評賞，以培養審美能力。\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			認識音樂的情境\r\n		</li>\r\n	</ul>\r\n	<p style="padding-left:30px;">\r\n		認識音樂的功能，並瞭解音樂與文化的關係。\r\n	</p>\r\n	<p style="padding-left:30px;">\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong>(三) 學習範圍</strong>\r\n	</p>\r\n	<ul>\r\n		<li>\r\n			學生進行音樂活動，發展共同能力：\r\n		</li>\r\n	</ul>\r\n	<ol start="1">\r\n		<ol start="1">\r\n			<li>\r\n				進行聲響設計，培養創造力、批判性思考和運算能力。\r\n			</li>\r\n			<li>\r\n				進行小組演奏活動，能培養溝通、協作、解決問題和自我管理能力。\r\n			</li>\r\n			<li>\r\n				進行音樂評賞，能發展批判性思考、研習和溝通能力。\r\n			</li>\r\n			<li>\r\n				運用音樂程式進行探索和創作活動，能培養創造力和運用資訊科技能力。\r\n			</li>\r\n		</ol>\r\n	</ol>\r\n	<p>\r\n		<br />\r\n	</p>\r\n	<p>\r\n		<strong>(四) 建立正確的價值觀和態度</strong>\r\n	</p>\r\n	<ol start="1">\r\n		<li>\r\n			理解音樂的內涵和感受其中的情?，培養審美能力及陶冶高尚情操。\r\n		</li>\r\n		<li>\r\n			演奏及評賞中國音樂，培養國民意識。\r\n		</li>\r\n		<li>\r\n			演唱及欣賞?劇?曲，瞭解和尊重本地傳統文化。\r\n		</li>\r\n		<li>\r\n			評賞不同文化的音樂，瞭解和尊重其他文化傳統和價值觀。\r\n		</li>\r\n		<li>\r\n			聆聽他人的創作和瞭解其演繹的特點，學習容納不同的觀點和取向。\r\n		</li>\r\n		<li>\r\n			鼓勵用正版的音樂程式及鐳射唱片，培養尊重知識產權的意識。\r\n		</li>\r\n		<li>\r\n			持之以恆地進行練習，提升演奏能力，培養堅毅的精神。\r\n		</li>\r\n		<li>\r\n			用心參與排練，準時出席，培養責任感和承擔精神。\r\n		</li>\r\n		<li>\r\n			參與分組創作聲響設計及合奏表演，培養合作及團隊精神。\r\n		</li>\r\n		<li>\r\n			遵守出席音樂會的禮儀，尊重別人的演出。\r\n		</li>\r\n		<li>\r\n			參與創作、演奏和聆聽音樂，養成終身愛好音樂的興趣。\r\n		</li>\r\n	</ol>\r\n	<p style="padding-left:30px;">\r\n		註：以上內容參照藝術教育學習領域 – 音樂科課程指引(P.12-13)\r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>', NULL, NULL, NULL, NULL, '2013-09-10', '2013-11-25 18:13:54', 0, 100);
INSERT INTO `tbl_stuteacher` (`a_id`, `a_type`, `a_title`, `link`, `target`, `a_content`, `a_encontent`, `a_cncontent`, `a_entitle`, `a_cntitle`, `a_date`, `add_time`, `year_id`, `order`) VALUES
(5, 2, '教師專業發展', 'http://', '_blank', '<div class="item-page">\r\n	<div class="page-header">\r\n		<h2>\r\n			教師專業發展\r\n		</h2>\r\n	</div>\r\n<!-- supertable debug output all exec 0.00005 gen 0.00001 tot 0.00006 end supertable debug output -->\r\n	<p>\r\n		&nbsp;\r\n	</p>\r\n	<meta content="text/html; charset=utf-8" http-equiv="Content-Type" />\r\n	<title>\r\n	</title>\r\n<br />\r\n	<p>\r\n		終身學習，不斷求新，是教學路途上，不可或缺的。老師們需要不斷學習，不斷更新知識，務求把教學工作做得最好，學生才能獲益，因此教師的專業發展在我校佔重要席位。\r\n	</p>\r\n	<p>\r\n		<br />\r\n每 年校方都會安排三天的教師發展日，讓教師學習教學的新方向和給予個人反思的機會。除此之外，校方也積極鼓勵同工參加教育局舉辦的校外進修課程(每年最少 達3小時)，而科主席也要參與本學科的進修，包括資訊科技教學，以提升自己的教學素質和配合資訊科技教學發展，務使教師們的教學工作上更臻完善。\r\n	</p>\r\n	<p>\r\n		&nbsp;\r\n	</p>\r\n	<p>\r\n		我校的校本培訓強調多元化發展及配合學校發展的需要。本年度更強調專科專教的進修、小班教學進修及教師提問技巧訓練，協助教師擴闊專科教學的知識領域及教學取向，以提升教學效益。\r\n	</p>\r\n	<p>\r\n		&nbsp;\r\n	</p>\r\n	<h2>\r\n		校本教師專業發展培訓記錄\r\n	</h2>\r\n	<p class="documenttip-jck">\r\n		2012-2013年度\r\n	</p>\r\n	<p>\r\n		&nbsp;\r\n	</p>\r\n	<table align="center" border="1" cellpadding="0" cellspacing="0" width="90%">\r\n		<tbody>\r\n			<tr>\r\n				<td bgcolor="#CCCCCC" width="4%">\r\n					&nbsp;\r\n				</td>\r\n				<td bgcolor="#CCCCCC" width="10%">\r\n					參加日期\r\n				</td>\r\n				<td bgcolor="#CCCCCC" valign="top" width="48%">\r\n					主題\r\n				</td>\r\n				<td bgcolor="#CCCCCC" width="38%">\r\n					主辦機構\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td bgcolor="#FFFFCC" width="4%">\r\n					1.\r\n				</td>\r\n				<td bgcolor="#FFFFCC" width="10%">\r\n					1/9/2012\r\n				</td>\r\n				<td bgcolor="#FFFFCC" valign="top" width="48%">\r\n					教師發展日<br />\r\n主題：建立復和關係、小班教學分享、團隊建立\r\n				</td>\r\n				<td bgcolor="#FFFFCC" width="38%">\r\n					復和中心、<br />\r\n聖公會基愛小學\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td bgcolor="#FFFFCC" width="4%">\r\n					2.\r\n				</td>\r\n				<td bgcolor="#FFFFCC" width="10%">\r\n					28/9/2012\r\n				</td>\r\n				<td bgcolor="#FFFFCC" valign="top" width="48%">\r\n					香港學生輔助會小學教師發展日\r\n				</td>\r\n				<td bgcolor="#FFFFCC" width="38%">\r\n					聖公會基