INSERT INTO `tbl_teacher` (`teacher_id`, `type`, `teacher_name`, `teacher_email`, `teacher_intro`, `teacher_login`, `password`, `allow_control_forum`, `admin`, `subject`, `title`, `show`, `order`, `docoment_name`) VALUES
(59, 'T', '柳子權', 'ckjlau@cpcydss.edu.hk', '教育工作是一項尊貴的召命，基督教讓我深信生命是有希望的，每一個人都可以去愛與被愛，每一個人都有他獨特的使命及承擔。不單相信「一個都不能少」，更深信「人人也可以學」。讓我們為培育下一代而鞠躬盡瘁。', 'ckjlau', 'timben413', 'Y', 'Y', '', '校長', 'Y', 1, '59.JPG'),
(64, 'T', '楊建強', 'williamcpc@cpcydss.edu.hk', '喜愛人，最喜愛年輕人，當然也愛上帝！除了人之外，我喜愛音樂，喜歡看海，也愛閱人、閱讀。自稱「邊青」牧師，皆因年紀已經不輕，是青年的邊緣，不願認老嘛！', 'cpcydimc004', '123456', 'N', 'N', '', '校牧、校董', 'Y', 3, '64.jpg'),
(73, 'T', '趙朗彤', 'chiulongtung@cpcydss.edu.hk', '我深信教育的重要性，而教育往往是潛移默化的。所以我希望能以老師的身份，與學生分享生活的點滴，啟發他們思考自己的人生，陪伴他們走成長的路。在耀道中學，一切都是從新做起；正如我們的學生，他們有無限的空間去發展。讓小苗健康茁壯地成長，正是我的盼望。', 'cpcydss004', '123456', 'Y', 'N', '中國語文學習領域', '教師', 'Y', 17, '73.JPG'),
(74, 'T', '崔家慧', 'estherchui@cpcydss.edu.hk', '我愛寧靜，因為靜下來可以好好計劃、反思、回憶、觀察，不斷重整自己，重獲力量去投入百變的人生旅程；我熱愛藝術和音樂，我希望與學生一起透過藝術找到自由和真我。', 'cpcydss005', '123456', 'Y', 'N', '藝術學習領域', '教師', 'Y', 23, '74.JPG'),
(77, 'T', '韓王鳳', 'wendyhon@cpcydss.edu.hk', '小時候時常迷路，所以特別喜歡以下的一節金句－『我未受苦而先走迷了路，現在卻遵行袮的話』。然而，對我亦有另一個意義，有機會再與您們分享。我是誰…….？韓小姐是也！', 'cpcydss008', '123456', 'Y', 'N', '', '文員', 'Y', 136, '77.jpg'),
(78, 'T', '江岸碧', 'dryicekong@cpcydss.edu.hk', '作為中文科老師的我，除了幫助學生鍛煉思維和表達能力外，教導學生明辨是非，培養品德情意及良好的心理質素也是責無旁貸的。\r\n    我從小接受基督教教育的薰陶，在成長路上，我曾體驗過父神和師友對我無比的關懷與啟迪。故此，我堅信我的學生若同樣感受被愛與尊重，生命會變得光彩不凡。每一個青少年人都是獨特和尊貴的，生命的塑造非一蹴即就，我願意和校內同工，共同承擔這建設生命的工程。\r\n', 'cpcydss009', '123456', 'Y', 'N', '中國語文、倫理及基督教教育學習領域', '教師', 'Y', 39, '78.jpg'),
(82, 'T', '林佩瓊', 'puiking@cpcydss.edu.hk', '我相信每個孩子均各有所長及有很多可欣賞的地方，盼望他們能認識及了解自己，能愛自己、愛別人，培養良好品格及建立正確的人生觀。', 'cpcydss013', '123456', 'Y', 'N', '體育學習領域', '教師', 'Y', 47, '82.JPG'),
(84, 'T', '李俊傑', 'ikkilee@cpcydss.edu.hk', '學習是一件愉快的事，而在現今資訊社會的年代，學習更不應只局限在教師的傳授。我期望能利用一些課堂活動，引發同學的好奇心和學習興趣，帶給大家一個有趣和嚴謹的學習經驗，從而培養良好的學習習慣、能力和態度，為終身學習打下基礎。', 'ikkilee', '123456', 'Y', 'Y', '科學學習領域', '教師', 'Y', 53, '84.jpg'),
(85, 'T', '李德成', 'ltsnoel@cpcydss.edu.hk', '我喜歡成為教師，因為我喜歡與青少年人相處，喜歡他們那份朝氣、活力及純真。我希望可以把我所學的知識和所累積的人生經驗與學生一同分享。在學業上，我要求學生專心上課、用心完成課業，盡學生應有的本分學習；在課外活動上，我會鼓勵學生多參與、多突破自己，從而建立自信，發揮自我潛能，培育領袖素質，使他們從中學會與人相處之道，學會尊重及接納別人的意見。\r\n我相信在家長悉心的照顧和老師努力的教導下，學生必能在耀道中學健康、愉快地成長。', 'cpcydss016', '123456', 'Y', 'N', '科學學習領域', '教師', 'Y', 54, '85.JPG'),
(87, 'T', '盧俊文', 'lochunman@cpcydss.edu.hk', '', 'cpcydss018', '123456', 'Y', 'N', '', '實驗室技術員', 'Y', 106, '87.JPG'),
(88, 'T', '羅惠明', 'chrisloyu@cpcydss.edu.hk', '“Fly away with books, explore the skies.”\r\n「讓該子手執好書，遨遊天空」將是我作為耀道中學圖書館主任的使命。\r\n盼望藉著套裝書早讀計劃、閱讀月挑戰站計劃、中英文圖書展、親炙作家講座、友校交流普讀營等不同層面的讀書活動，帶領您們的孩子一起放眼四海，飛越天際。', 'cpcydss019', '123456', 'Y', 'N', '中國語文、倫理及基督教教育學習領域', '圖書館主任', 'Y', 60, '88.JPG'),
(91, 'T', '吳穎詩', 'ceciliang@cpcydss.edu.hk', '我是負責本校音樂課程的老師。我一直都希望我的學生能夠以音樂來敬拜上帝，因為我認為音樂是讚美、敬拜上帝的途徑，而且也可以傳揚神的救恩。另外，詩歌的旋律優美，從中亦可讓我們學習欣賞音樂、欣賞作曲作詞者以及神的智慧。所以，在音樂課堂中，除了教授傳統樂理、牧童笛吹奏外，每一課堂我都會有一首新的詩歌介紹給學生。', 'cpcydss022', '123456', 'Y', 'N', '藝術、倫理及基督教教育學習領域', '教師', 'Y', 69, '91.JPG'),
(94, 'T', '尹再英', 'emmywan@cpcydss.edu.hk', '我相信老師之角色對於中學生的發展是很重要的，除了課文知識的教授外，影響他們最深遠的是品德的培養。故我希望同學在耀道中學能一方面享受學習帶來的樂趣，發展自己的潛能；另一方面能明辨是非，有高尚的人格，做一個有責任心，努力向上的好公民。', 'cpcydss025', '123456', 'Y', 'N', '數學學習領域', '教師', 'Y', 87, '94.jpg'),
(95, 'T', '王曦妍', 'hhywong@cpcydss.edu.hk', '能與您及您的孩子相遇於耀道中學，自是難得的緣份。我們面前的道路或許不是最平坦的，但如中文大學新亞書院的院歌中所道：「艱險我奮進，困乏我多情，千斤擔子兩肩挑，趁青春結隊，向前行」。希望我們可一直同行，為孩子營造一片最美麗的天空，令他們的中學生活豐盛難忘。', 'cpcydss026', '123456', 'Y', 'N', '通識學習領域', '教師', 'Y', 90, '95.jpg'),
(96, 'T', '黃皓昕', 'nathanhywong@cpcydss.edu.hk', '"Life is like a game of Tennis, you can''t win without serving well."\r\n    Anyone who knows that a sensational service game can give you the advantage and anyone who knows Nathan knows that his characteristics are based on service. Being the only teacher born and raised in Canada, I''d like to think that I add another different perspective to the already dynamic team of teachers. I love sharing my interests in sports, music, cooking, video games, computer, art, toys and many more with the students realizing we share a lot in common --- but of course it’s all done in English!', 'cpcydss027', '123456', 'Y', 'N', 'English Language Education', '教師', 'Y', 93, '96.jpg'),
(101, 'T', '黃永耀', 'keithwwy@cpcydss.edu.hk', '我們要永不言敗\r\n    我深信每個孩子都是可教的，透過身教，希望培養出學生積極、正面及永不言敗的人生觀。作為老師的我，除了教授學科上的知識外，亦希望為學生提供多元化的生活體驗，培養他們完善的人格及高尚的道德情操，並懂得珍惜自己所擁有的一切。\r\n    興趣:足球、遠足及製作機械人。\r\n', 'cpcydss032', '123456', 'Y', 'N', '科技學習領域', '教師', 'Y', 95, '101.JPG'),
(105, 'T', '楊李雪梅', 'carolyeung@cpcydss.edu.hk', '我喜歡定下目標以及完成目標，我喜歡吸收知識，令自己成為一個有用的人。我認為教育工作是一項專業的工作，教師的大使命不單是傳遞知識及學科內容；與同學分享一些人生的哲理及正確的價值觀也是十分重要的。本人期望以真誠愛同學，尊重同學個別的差異，培養同學建立自信心及以基督的愛愛護同學。', 'cpcydss036', '123456', 'Y', 'N', 'English Language Education', '教師', 'Y', 97, '105.jpg'),
(110, 'T', 'Andrew William Dawson', 'andrewdawson@cpcydss.edu.hk', 'I am proud to have been welcomed as a staff member of CPC Yao Dao Secondary School.  Coming from Australia, “the land down under”, I hope to be able to bring my own unique experiences to my role as ‘Native English Teacher’.  I have a passion for education and have experience teaching in country Australia, a remote Australian Aboriginal community and in the UK.  My interests are varied, but I am enthusiastic about different cultures, the outdoors, English literature, cooking, history, fishing and coffee.  I love to travel and have had rich experiences exploring Asia, Europe and North America.  I look forward to an exciting year, but please accept my inability to speak Cantonese as a trade off for my native fluency in English.', 'cpcydss203', '234567', 'Y', 'N', 'English Language Education', '教師', 'Y', 5, '110.jpg'),
(111, 'T', '陳瑞宜', 'annchansy@cpcydss.edu.hk', '希望耀道每一位同學在自己生命中創造奇蹟，\r\n發出光芒。', 'cpcydss204', '234567', 'Y', 'N', '社會、人文、倫理及基督教教育學習領域', '教師', 'Y', 13, '111.jpg'),
(113, 'T', '符月瑩', 'janetfu@cpcydss.edu.hk', '我喜歡跟學生分享身邊發生的小事，讓他們明白書本的知識其實有限，只要用心觀察，一花一草也可以成為我們的學習對象。', 'cpcydss206', '234567', 'Y', 'N', '中國語文學習領域', '教師', 'Y', 25, '113.jpg'),
(114, 'T', '馮巧茵', 'ariesfung@cpcydss.edu.hk', '愛山、水、星、書、電影、戲劇、攝影、遊中國。愛玩，但更愛學習新事物，因為可以助我拓闊眼界、認識世情。相信生命本身就是歷奇，因此喜愛教師這份工作，因為可以讓我碰觸到許許多多精采的生命個體。\r\n    我所愛的學生們，樂於跟你們共同學習、一同努力成長﹗', 'cpcydss207', '234567', 'N', 'N', '數學學習領域', '教師', 'Y', 26, '114.jpg'),
(115, 'T', '洪佩瓊', 'redcatjoey@cpcydss.edu.hk', 'It is my pleasure to share my knowledge, my life lessons, my godly passions, and the GOOD NEWS with all of you. \r\n    Let us discover the point of life and live for it together. ^^', 'cpcydss208', '234567', 'Y', 'N', '中國語文學習領域', '教師', 'Y', 37, '115.jpg'),
(116, 'T', '李裕庭', 'terrylee@cpcydss.edu.hk', '我很同意前中國女排教練袁偉民的一句說話：「艱苦，是人生路上誰都會遇到的一塊石頭。強者用它來磨礪寶劍的鋒刃，弱者被它擋住了前進的腳步」。\r\n    我們遇到艱苦，要勇敢面對，這亦是神對我們的磨練。當我們靠著神的力量，可以從苦難中成長。「耶和華是我的力量，是我的盾牌，我心裡倚靠祂，就得幫助」。(詩篇28:7)', 'cpcydss209', '234567', 'Y', 'N', '體育學習領域', '教師', 'Y', 55, '116.JPG'),
(117, 'T', '鮑世賢', 'tonipau@cpcydss.edu.hk', '我深信行動是成功的階梯，行動越多，登得越高，所以不要只坐說不能或做不到。同學們要多作嘗試，嘗試把impossible（不可能）變成I''m possible （我是可能的）!', 'cpcydss210', '234567', 'Y', 'N', '體育學習領域', '教師', 'Y', 72, '117.JPG'),
(118, 'T', '岑嘉智', 'sammysam@cpcydss.edu.hk', '在這個資訊爆炸的社會，我們要學習的，已不單單是書上的內容，更重要的，是我們懂得與身邊的人合作，一起追尋新知識新事物的方法。你準備好了麼？你願意與我一同走進這個充滿挑戰的世代嗎？一起上路吧！\r\nSam Sir:\r\nhttp://www.sammysam.com', 'cpcydss211', '234567', 'Y', 'N', '技科及通識學習領域', '教師', 'Y', 73, '118.jpg'),
(119, 'T', '許美龍', 'shuemeilung@cpcydss.edu.hk', '耀道不單是一個讓同學成長的地方，也是一個讓教職員成長的地方。雖然成長不免要經歷掙扎、克服困難，但十分慶幸能與同學一起在這裡成長，願同學珍惜每個學習機會。上帝的愛必與我們同在，幫助我們跨過困難。', 'cpcydss212', '234567', 'Y', 'N', '中國語文學習領域', '教師', 'Y', 74, '119.JPG'),
(120, 'T', '蘇佩詩', 'rebeccaso@cpcydss.edu.hk', '作為一位老師，除了傳授學生知識外，還希望看著他們快樂地成長，讓他們有著正面的人生觀，對社會有承擔。而作為一位數學老師，更希望我的學生可通過數學擴闊他們的視野，繼而和數學成為好朋友。願同學們在耀道發揮自己所長，茁壯成長，並為耀道中學播下快樂和幸福的種子。', 'cpcydss213', '234567', 'Y', 'N', '數學學習領域', '教師', 'Y', 75, '120.jpg'),
(121, 'T', '譚嘉豪', 'wilsontamkh@cpcydss.edu.hk', '"I find it very rewarding and satisfying to work at Yao Dao. The people I work with and the students I meet are excellent teachers for me. It is my pleasure to meet them all."', 'cpcydss214', '234567', 'Y', 'N', 'English Language Education', '教師', 'Y', 76, '121.JPG'),
(122, 'T', '溫玉嫻', 'wyhwyh@cpcydss.edu.hk', '我喜歡青年人那份赤子之心和勇闖無懼的勇氣。\r\n我更喜歡跟他們一起經歷成長路上所遇到的各種障礙。給予鼓勵、支持和接納，並教導他們當走的路，是一件既滿足又富挑戰性的工作。', 'cpcydss215', '234567', 'Y', 'N', '科學學習領域', '教師', 'Y', 89, '122.jpg'),
(124, 'T', '姚寶瑩', 'rebeccapyy@cpcydss.edu.hk', '每個人的生命都是獨特的，然而生命的價值貴乎親身經歷、具體感受、反思實踐、活在當下。人生就像是沒有劇本的一齣戲，實踐的經驗告訴我們，作為一位「演員」，是要靠勤奮、耐勞、恆心與毅力才能成功的，單靠外表形象、靠運氣，是無法長久的。', 'cpcydss217', '234567', 'Y', 'N', '科技學習領域', '教師', 'Y', 101, '124.jpg'),
(142, 'T', '陳福全', 'john@eclass.cpcydss.edu.hk', '', 'cpcdyss229', 'cpcdyss', 'N', 'N', '', '教學助理', 'Y', 108, '142.jpg'),
(144, 'T', '顏狄衛', 'ngantw@cpcydss.edu.hk', '', 'cpcdyss231', 'cpcydss', 'N', 'N', '', '實驗室技術員', 'Y', 107, '144.jpg'),
(146, 'T', '羅友偉', 'samlaw@eclass.cpcydss.edu.hk', '', 'cpcdyss234', '1234', 'N', 'N', '', '資訊科技助理', 'Y', 129, '146.jpg'),
(147, 'T', '孔昭恆', 'chhung@eclass.cpcydss.edu.hk', 'I believe that all students are educable and they have great potentials. ', 'cpcdyss235', '1234', 'N', 'N', 'English Language Education', '教師', 'Y', 34, '147.JPG'),
(149, 'T', '呂光耀', 'kylui2@eclass.cpcydss.edu.hk', '相信是神一步一步的帶領，讓我有機會成為耀道中學的一份子。教育是我的理想、事奉與興趣。能與一班愛教育愛學生的同工和我們的學生一起成長、一起經歷生命的喜與憂，是我一生所追求的。盼望我們人人都有夢想，並且能堅持到底！', 'cpcdyss237', '1234', 'N', 'N', '社會及人文學習領域', '教師', 'Y', 61, '149.jpg'),
(153, 'T', '莫文烽', 'marvinmok@eclass.cpcydss.edu.hk', '「海鷗要高飛，必先遠退。花蜜要香醇，必先久釀。人要一展抱負，必先充實內涵。」學校是讓同學們充實的地方，珍惜於耀道中學的時光，好好充實自己，找出你們的方向，我期望耀道中學的同學他日能一展所長，回饋社會。', 'cpcdyss241', '1234', 'N', 'N', '科技及通識學習領域', '教師', 'Y', 68, '153.jpg'),
(154, 'T', '莫志強', 'samuelmok@eclass.cpcydss.edu.hk', '處世不可退而求其次。', 'cpcdyss242', '1234', 'N', 'N', '科學學習領域', '教師', 'Y', 67, '154.jpg'),
(156, 'T', '陳愷殷', 'oranchan@eclass.cpcydss.edu.hk', '做老師不單是教書的。因為我喜歡「教書」，也喜歡「教人」，所以投身教育。希望耀道同學能在一班有教育熱誠的老師陪伴下，不斷成長!', 'cpcdyss244', '1234', 'N', 'N', '通識、社會及人文學習領域', '教師', 'Y', 12, '156.JPG'),
(157, 'T', '陳昌盛', 'louischan@eclass.cpcydss.edu.hk', '每個人都有其才能，只是有些人的才能尚未被發掘；每個人都其優點，只是有些人的優點尚未被他人所發現，甚至連自己也沒有發現。我希望來到耀中能與同學一同發掘他們的才能；發現彼此的優點，讓大家能彼此互相欣賞、一起成長。', 'cpcdyss245', '1234', 'N', 'N', '數學、倫理及基督教教育學習領域', '教師', 'Y', 10, '157.jpg'),
(158, 'T', '陳穎嫺', 'prudence@eclass.cpcydss.edu.hk', '年青人是不足而又豐富的一群。不足，是因為他們在學養和待人接物的技巧上，仍然有學習進步的空間；豐富，是因為他們擁有無限的創造力和活力。期望同學在這一學年能夠竭力求追求知識的長進，並積極發展個人潛能，提升自己。', 'cpcdyss246', '1234', 'N', 'N', '中國語文學習領域', '教師', 'Y', 14, '158.JPG'),
(160, 'T', '湛紀賢', 'chamky@eclass.cpcydss.edu.hk', '年青人有很多空間可以成長和被塑造，我相信幫助各同學發現及發展自己的價值及長處是老師的責任， 我相信各同學都是「得o既」\r\n願意各同學能認識創造「你」的主耶穌基督，使你無論或順或逆，都滿有能力和盼望面對，正如詩歌所說：「誰曾應許一生不撇下我， 每段窄路誰陪我去走過」 主是「你」的避難所，「你」的盾牌，「你」的詩歌，更是「你」隨時的幫助。 \r\n湛老師願意跟大家分享經歷，無論是信仰上、學業上，都想跟大家一同成長。感謝神帶領我來到耀道中學作各位同學的老師，希望在這一年裡大家彼此享受一同相處學習的時間。\r\n', 'cpcdyss248', '1234', 'N', 'N', '科學及通識教育學習領域', '教師', 'Y', 9, '160.jpg'),
(161, 'T', '馮嘉蕙', 'anthea@eclass.cpcydss.edu.hk', '希望能將自己所學的帶給學生，令他們有所提昇﹔更可陪伴他們經歷不同的困難。', 'cpcdyss249', '1234', 'Y', 'N', '中國語文學習領域', '教師', 'Y', 27, '161.JPG'),
(164, 'T', '雷穎嫺', 'ericalui@eclass.cpcydss.edu.hk', '青年人面對的是瞬息萬變的社會，他們需要好好裝備自己，學習處世的良好態度，更要學會自強不息，盼望耀中的同學們，孝敬父母，敬師愛友，珍惜光陰，努力向上，使自己的生命為神發光發熱！', 'cpcdyss252', '1234', 'N', 'N', '中國語文及科技學習領域', '教師', 'Y', 62, '164.jpg'),
(165, 'T', '劉俐君', 'avalau@eclass.cpcydss.edu.hk', '給我可愛的學生：\r\n你們都是善良活潑的，若有人給予支持與引導，定會成為出色的人。希望你們的成長旅程，能讓我參與其中，作你們的同路人。', 'cpcdyss253', '1234', 'Y', 'N', '社會、人文及科技學習領域', '教師', 'Y', 52, '165.jpg'),
(166, 'T', '簡偉基', 'kanwk2004@eclass.cpcydss.edu.hk', '我很喜歡與少年人交談，因我覺得他們很坦率，又有活力，雖然有時會衝動一些，但大多數都是善良守規的。能陪伴他們一起成長是一種難以形容的喜悅。', 'cpcdyss254', '1234', 'N', 'N', '數學學習領域', '教師', 'Y', 38, '166.jpg'),
